import React,{ useState} from 'react';
import StoreProducts from './StoreProducts.json';

const FilterFunc = () => {
    const [items,setItems] = useState(StoreProducts);
    console.log(items);
  return (
    <div className='container mt-5'>
        <h1>All Products</h1>
        <div className='row'>
            {items.map((val)=>(
                <div className='col-md-3'>
                    <div class="card" >
                         <img src={val.image} class="card-img-top img-fluid" alt="..."/>
                         <div class="card-body">
                         <h5><span class="badge bg-primary">{val.packaging}</span></h5>
                            <h5 class="card-title">{val.name.substring(0,15)}</h5>
                            <p class="card-text">{val.description}</p>

                         </div>
                    </div>
                </div>
            ))}

        </div>
    </div>
  )
}

export default FilterFunc
